$(document).ready(function() {
    App.init();
});

const App = {
    init() {
        this.initNavigation();
        this.initTheme();
        this.initSearch();
        this.initFAB();
        this.initScrollEffects();
        this.initRuntimeCounter();
        this.initSmoothTransition();
        this.bindEvents();
        // 移除 initContentLoader，不再加载假数据
    },

    initNavigation() {
        const $header = $('.header');
        const $mobileMenuBtn = $('.mobile-menu-btn');
        const $navMenu = $('.nav-menu');
        const $navLinks = $('.nav-link');
        let lastScrollTop = 0;
        let ticking = false;

        const updateHeader = () => {
            const scrollTop = $(window).scrollTop();

            if (scrollTop > 100) {
                $header.addClass('scrolled');
            } else {
                $header.removeClass('scrolled');
            }

            if (scrollTop > lastScrollTop && scrollTop > 200) {
                $header.css('transform', 'translateY(-100%)');
            } else {
                $header.css('transform', 'translateY(0)');
            }
            lastScrollTop = scrollTop;
            ticking = false;
        };

        $(window).on('scroll', () => {
            if (!ticking) {
                requestAnimationFrame(updateHeader);
                ticking = true;
            }
        });

        $mobileMenuBtn.on('click', () => {
            $navMenu.toggleClass('active');
            const icon = $mobileMenuBtn.find('.material-icons').text();
            $mobileMenuBtn.find('.material-icons').text(
                icon === 'menu' ? 'close' : 'menu'
            );
        });

        $navLinks.on('click', function(e) {
            const target = $(this).attr('href');

            if (target && target.startsWith('#')) {
                e.preventDefault();
                $navLinks.removeClass('active');
                $(this).addClass('active');

                $navMenu.removeClass('active');
                $mobileMenuBtn.find('.material-icons').text('menu');

                const $target = $(target);
                if ($target.length) {
                    $('html, body').animate({
                        scrollTop: $target.offset().top - 80
                    }, 600);
                }
            } else {
              $navMenu.removeClass('active');
                $mobileMenuBtn.find('.material-icons').text('menu');
            }
        });
    },

    initTheme() {
        const $body = $('body');
        const savedTheme = localStorage.getItem('theme') || 'light';
        const savedFontSize = localStorage.getItem('fontSize') || 'normal';

        // 首先应用保存的主题
        this.setTheme(savedTheme);
        this.setFontSize(savedFontSize);

        // 使用事件委托来确保按钮点击始终有效
        $(document).off('click.themeToggle').on('click.themeToggle', '.theme-toggle', (e) => {
            e.preventDefault();
            e.stopPropagation();

            // 直接从 localStorage 读取当前主题，而不是从DOM判断
            const currentTheme = localStorage.getItem('theme') || 'light';
            const newTheme = currentTheme === 'light' ? 'dark' : 'light';

            App.setTheme(newTheme);
            localStorage.setItem('theme', newTheme);
        });

        // 字体大小切换也使用事件委托
        let fontSizeIndex = ['small', 'normal', 'large'].indexOf(savedFontSize);
        const fontSizes = ['small', 'normal', 'large'];

        $(document).off('click.fontSizeToggle').on('click.fontSizeToggle', '.font-size', (e) => {
            e.preventDefault();
            e.stopPropagation();

            fontSizeIndex = (fontSizeIndex + 1) % fontSizes.length;
            this.setFontSize(fontSizes[fontSizeIndex]);
            localStorage.setItem('fontSize', fontSizes[fontSizeIndex]);
        });
    },

    setTheme(theme) {
        const $body = $('body');
        const $themeIcon = $('.theme-toggle .material-icons');

        // 清除所有主题相关的类
        $body.removeClass('light-theme dark-theme');

        // 只在暗黑模式时添加dark-theme类，光亮模式不添加任何主题类
        if (theme === 'dark') {
            $body.addClass('dark-theme');
        }

        // 更新图标
        setTimeout(() => {
            if ($themeIcon.length) {
                $themeIcon.text(theme === 'light' ? 'dark_mode' : 'light_mode');
            }
        }, 10);

    },

    setFontSize(size) {
        const $body = $('body');
        $body.removeClass('font-size-small font-size-large');

        if (size !== 'normal') {
            $body.addClass(`font-size-${size}`);
        }
    },

    initSearch() {
        const $searchBtn = $('.search-btn');
        const $searchContainer = $('.search-container');
        const $searchClose = $('.search-close');
        const $searchInput = $('.search-input');
        const $searchSubmit = $('.search-submit');

        $searchBtn.on('click', () => {
            $searchContainer.addClass('active');
            setTimeout(() => $searchInput.focus(), 300);
        });

        $searchClose.on('click', () => {
            $searchContainer.removeClass('active');
            $searchInput.val('');
        });

        $(document).on('keydown', (e) => {
            if (e.key === 'Escape' && $searchContainer.hasClass('active')) {
                $searchContainer.removeClass('active');
                $searchInput.val('');
            }
        });

        $searchSubmit.on('click', () => {
            this.performSearch($searchInput.val());
        });

        $searchInput.on('keypress', (e) => {
            if (e.key === 'Enter') {
                this.performSearch($searchInput.val());
            }
        });

        $(document).on('click', (e) => {
            if (!$searchContainer.is(e.target) &&
                $searchContainer.has(e.target).length === 0 &&
                !$searchBtn.is(e.target) &&
                $searchBtn.has(e.target).length === 0) {
                $searchContainer.removeClass('active');
            }
        });
    },

    performSearch(query) {
        if (!query.trim()) return;
        console.log('搜索:', query);
        $('.search-overlay').removeClass('active');
    },

    initFAB() {
        const $fabContainer = $('.fab-container');
        const $mainFab = $('.main-fab');
        const $backToTop = $('.back-to-top');

        $mainFab.on('click', () => {
            $fabContainer.toggleClass('active');
        });

        $backToTop.on('click', () => {
            $('html, body').animate({
                scrollTop: 0
            }, {
                duration: 600,
                easing: 'easeOutCubic'
            });
            $fabContainer.removeClass('active');
        });

        $(window).on('scroll', $.throttle(100, () => {
            const scrollTop = $(window).scrollTop();
            if (scrollTop > 200) {
                $backToTop.addClass('show');
            } else {
                $backToTop.removeClass('show');
            }
        }));

        $(document).on('click', (e) => {
            if (!$fabContainer.is(e.target) && $fabContainer.has(e.target).length === 0) {
                $fabContainer.removeClass('active');
            }
        });
    },

    initScrollEffects() {
        let scrollTicking = false;

        const updateScrollEffects = () => {
            $('.fade-in-up:not(.visible)').each(function() {
                const $element = $(this);
                const elementTop = $element.offset().top;
                const windowBottom = $(window).scrollTop() + $(window).height();

                if (elementTop < windowBottom - 50) {
                    $element.addClass('visible');
                }
            });
            scrollTicking = false;
        };

        $(window).on('scroll', () => {
            if (!scrollTicking) {
                requestAnimationFrame(updateScrollEffects);
                scrollTicking = true;
            }
        });
    },

    initRuntimeCounter() {
        const startDate = new Date('2025-01-01 00:00:00');

        const updateRuntime = () => {
            const now = new Date();
            const diff = now - startDate;

            const days = Math.floor(diff / (1000 * 60 * 60 * 24));
            const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

            $('#runtime').text(`${days}天${hours}小时${minutes}分钟`);
        };

        updateRuntime();
        setInterval(updateRuntime, 60000);
    },

    initSmoothTransition() {
        // 为页面添加平滑过渡效果
        $('body').addClass('smooth-transition');

        // 创建加载条元素
        const $loadingBar = $('<div class="page-loading-bar"></div>');
        $('body').append($loadingBar);

        // 为所有内部链接添加淡出效果，包括导航链接
        $(document).on('click', 'a:not([href^="#"]):not([target="_blank"])', function(e) {
            const href = $(this).attr('href');
            const currentHost = window.location.hostname;

            // 检查是否为内部链接
            if (href) {
                try {
                    const linkUrl = new URL(href, window.location.origin);
                    const isInternal = linkUrl.hostname === currentHost ||
                                     href.startsWith('/') ||
                                     !href.includes('://');

                    // 排除特殊链接
                    if (isInternal &&
                        !href.startsWith('mailto:') &&
                        !href.startsWith('tel:') &&
                        !href.startsWith('javascript:')) {

                        e.preventDefault();

                        // 显示加载条并开始进度动画
                        $loadingBar.addClass('active');

                        // 模拟进度更新
                        setTimeout(() => {
                            $loadingBar.addClass('progress-30');
                        }, 100);

                        setTimeout(() => {
                            $loadingBar.addClass('progress-60');
                        }, 200);

                        // 延迟跳转
                        setTimeout(() => {
                            window.location.href = href;
                        }, 300);
                    }
                } catch (error) {
                    // 如果URL解析失败，检查是否为相对路径
                    if (!href.includes('://') &&
                        !href.startsWith('mailto:') &&
                        !href.startsWith('tel:')) {

                        e.preventDefault();
                        $loadingBar.addClass('active');

                        setTimeout(() => {
                            $loadingBar.addClass('progress-30');
                        }, 100);

                        setTimeout(() => {
                            $loadingBar.addClass('progress-60');
                        }, 200);

                        setTimeout(() => {
                            window.location.href = href;
                        }, 300);
                    }
                }
            }
        });

        // 页面加载完成时完成加载条动画
        $(window).on('load', () => {
            $loadingBar.addClass('progress-100');

            // 延迟隐藏加载条，添加淡出效果
            setTimeout(() => {
                $loadingBar.addClass('fade-out');

                // 等待淡出动画完成后再移除所有类
                setTimeout(() => {
                    $loadingBar.removeClass('active progress-30 progress-60 progress-80 progress-100 fade-out');
                }, 300);

                // 页面切换后重新应用保存的主题状态
                const savedTheme = localStorage.getItem('theme') || 'light';
                App.setTheme(savedTheme);

                // 重新初始化主题功能，确保按钮正常工作
                App.initTheme();
            }, 500);
        });

        // DOM 准备完成时的中间进度
        $(document).ready(() => {
            if ($loadingBar.hasClass('active')) {
                $loadingBar.addClass('progress-80');
            }
        });
    },

    updateActiveNav() {
        // 更新导航菜单的激活状态
        const currentPath = window.location.pathname;
        $('.nav-link').removeClass('active');

        $('.nav-link').each(function() {
            const linkPath = new URL($(this).attr('href'), window.location.origin).pathname;
            if (linkPath === currentPath) {
                $(this).addClass('active');
            }
        });
    },

    bindEvents() {
        // PJAX 已经处理文章卡片点击，移除重复的处理器

        // 为分页按钮绑定事件 - 移除阻止默认行为的代码
        // PJAX 会自动处理分页链接
    }
};

// 保留工具函数
$.throttle = function(delay, fn) {
    let timeoutID = null;
    let lastExec = 0;

    function wrapper() {
        const elapsed = +new Date() - lastExec;
        const args = Array.prototype.slice.call(arguments);
        const exec = () => {
            lastExec = +new Date();
            fn.apply(this, args);
        };

        clearTimeout(timeoutID);
        if (elapsed > delay) {
            exec();
        } else {
            timeoutID = setTimeout(exec, delay - elapsed);
        }
    }
    return wrapper;
};

$.easing.easeOutCubic = function (x, t, b, c, d) {
    return c*((t=t/d-1)*t*t + 1) + b;
};

$(document).ready(function() {
    App.init();
});

const App = {
    init() {
        this.initNavigation();
        this.initTheme();
        this.initSearch();
        this.initFAB();
        this.initScrollEffects();
        this.initRuntimeCounter();
        this.initSmoothTransition();
        this.bindEvents();
        // 移除 initContentLoader，不再加载假数据
    },

    initNavigation() {
        const $header = $('.header');
        const $mobileMenuBtn = $('.mobile-menu-btn');
        const $navMenu = $('.nav-menu');
        const $navLinks = $('.nav-link');
        let lastScrollTop = 0;
        let ticking = false;

        const updateHeader = () => {
            const scrollTop = $(window).scrollTop();

            if (scrollTop > 100) {
                $header.addClass('scrolled');
            } else {
                $header.removeClass('scrolled');
            }

            if (scrollTop > lastScrollTop && scrollTop > 200) {
                $header.css('transform', 'translateY(-100%)');
            } else {
                $header.css('transform', 'translateY(0)');
            }
            lastScrollTop = scrollTop;
            ticking = false;
        };

        $(window).on('scroll', () => {
            if (!ticking) {
                requestAnimationFrame(updateHeader);
                ticking = true;
            }
        });

        $mobileMenuBtn.on('click', () => {
            $navMenu.toggleClass('active');
            const icon = $mobileMenuBtn.find('.material-icons').text();
            $mobileMenuBtn.find('.material-icons').text(
                icon === 'menu' ? 'close' : 'menu'
            );
        });

        $navLinks.on('click', function(e) {
            const target = $(this).attr('href');

            if (target && target.startsWith('#')) {
                e.preventDefault();
                $navLinks.removeClass('active');
                $(this).addClass('active');

                $navMenu.removeClass('active');
                $mobileMenuBtn.find('.material-icons').text('menu');

                const $target = $(target);
                if ($target.length) {
                    $('html, body').animate({
                        scrollTop: $target.offset().top - 80
                    }, 600);
                }
            } else {
              $navMenu.removeClass('active');
                $mobileMenuBtn.find('.material-icons').text('menu');
            }
        });
    },

    initTheme() {
        const $body = $('body');
        const savedTheme = localStorage.getItem('theme') || 'light';
        const savedFontSize = localStorage.getItem('fontSize') || 'normal';

        // 首先应用保存的主题
        this.setTheme(savedTheme);
        this.setFontSize(savedFontSize);

        // 使用事件委托来确保按钮点击始终有效
        $(document).off('click.themeToggle').on('click.themeToggle', '.theme-toggle', (e) => {
            e.preventDefault();
            e.stopPropagation();

            // 直接从 localStorage 读取当前主题，而不是从DOM判断
            const currentTheme = localStorage.getItem('theme') || 'light';
            const newTheme = currentTheme === 'light' ? 'dark' : 'light';

            App.setTheme(newTheme);
            localStorage.setItem('theme', newTheme);
        });

        // 字体大小切换也使用事件委托
        let fontSizeIndex = ['small', 'normal', 'large'].indexOf(savedFontSize);
        const fontSizes = ['small', 'normal', 'large'];

        $(document).off('click.fontSizeToggle').on('click.fontSizeToggle', '.font-size', (e) => {
            e.preventDefault();
            e.stopPropagation();

            fontSizeIndex = (fontSizeIndex + 1) % fontSizes.length;
            this.setFontSize(fontSizes[fontSizeIndex]);
            localStorage.setItem('fontSize', fontSizes[fontSizeIndex]);
        });
    },

    setTheme(theme) {
        const $body = $('body');
        const $themeIcon = $('.theme-toggle .material-icons');

        // 清除所有主题相关的类
        $body.removeClass('light-theme dark-theme');

        // 只在暗黑模式时添加dark-theme类，光亮模式不添加任何主题类
        if (theme === 'dark') {
            $body.addClass('dark-theme');
        }

        // 更新图标
        setTimeout(() => {
            if ($themeIcon.length) {
                $themeIcon.text(theme === 'light' ? 'dark_mode' : 'light_mode');
            }
        }, 10);

    },

    setFontSize(size) {
        const $body = $('body');
        $body.removeClass('font-size-small font-size-large');

        if (size !== 'normal') {
            $body.addClass(`font-size-${size}`);
        }
    },

    initSearch() {
        const $searchBtn = $('.search-btn');
        const $searchContainer = $('.search-container');
        const $searchClose = $('.search-close');
        const $searchInput = $('.search-input');
        const $searchSubmit = $('.search-submit');

        $searchBtn.on('click', () => {
            $searchContainer.addClass('active');
            setTimeout(() => $searchInput.focus(), 300);
        });

        $searchClose.on('click', () => {
            $searchContainer.removeClass('active');
            $searchInput.val('');
        });

        $(document).on('keydown', (e) => {
            if (e.key === 'Escape' && $searchContainer.hasClass('active')) {
                $searchContainer.removeClass('active');
                $searchInput.val('');
            }
        });

        $searchSubmit.on('click', () => {
            this.performSearch($searchInput.val());
        });

        $searchInput.on('keypress', (e) => {
            if (e.key === 'Enter') {
                this.performSearch($searchInput.val());
            }
        });

        $(document).on('click', (e) => {
            if (!$searchContainer.is(e.target) &&
                $searchContainer.has(e.target).length === 0 &&
                !$searchBtn.is(e.target) &&
                $searchBtn.has(e.target).length === 0) {
                $searchContainer.removeClass('active');
            }
        });
    },

    performSearch(query) {
        if (!query.trim()) return;
        console.log('搜索:', query);
        $('.search-overlay').removeClass('active');
    },

    initFAB() {
        const $fabContainer = $('.fab-container');
        const $mainFab = $('.main-fab');
        const $backToTop = $('.back-to-top');

        $mainFab.on('click', () => {
            $fabContainer.toggleClass('active');
        });

        $backToTop.on('click', () => {
            $('html, body').animate({
                scrollTop: 0
            }, {
                duration: 600,
                easing: 'easeOutCubic'
            });
            $fabContainer.removeClass('active');
        });

        $(window).on('scroll', $.throttle(100, () => {
            const scrollTop = $(window).scrollTop();
            if (scrollTop > 200) {
                $backToTop.addClass('show');
            } else {
                $backToTop.removeClass('show');
            }
        }));

        $(document).on('click', (e) => {
            if (!$fabContainer.is(e.target) && $fabContainer.has(e.target).length === 0) {
                $fabContainer.removeClass('active');
            }
        });
    },

    initScrollEffects() {
        let scrollTicking = false;

        const updateScrollEffects = () => {
            $('.fade-in-up:not(.visible)').each(function() {
                const $element = $(this);
                const elementTop = $element.offset().top;
                const windowBottom = $(window).scrollTop() + $(window).height();

                if (elementTop < windowBottom - 50) {
                    $element.addClass('visible');
                }
            });
            scrollTicking = false;
        };

        $(window).on('scroll', () => {
            if (!scrollTicking) {
                requestAnimationFrame(updateScrollEffects);
                scrollTicking = true;
            }
        });
    },

    initRuntimeCounter() {
        const startDate = new Date('2025-01-01 00:00:00');

        const updateRuntime = () => {
            const now = new Date();
            const diff = now - startDate;

            const days = Math.floor(diff / (1000 * 60 * 60 * 24));
            const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

            $('#runtime').text(`${days}天${hours}小时${minutes}分钟`);
        };

        updateRuntime();
        setInterval(updateRuntime, 60000);
    },

    initSmoothTransition() {
        // 为页面添加平滑过渡效果
        $('body').addClass('smooth-transition');

        // 创建加载条元素
        const $loadingBar = $('<div class="page-loading-bar"></div>');
        $('body').append($loadingBar);

        // 为所有内部链接添加淡出效果，包括导航链接
        $(document).on('click', 'a:not([href^="#"]):not([target="_blank"])', function(e) {
            const href = $(this).attr('href');
            const currentHost = window.location.hostname;

            // 检查是否为内部链接
            if (href) {
                try {
                    const linkUrl = new URL(href, window.location.origin);
                    const isInternal = linkUrl.hostname === currentHost ||
                                     href.startsWith('/') ||
                                     !href.includes('://');

                    // 排除特殊链接
                    if (isInternal &&
                        !href.startsWith('mailto:') &&
                        !href.startsWith('tel:') &&
                        !href.startsWith('javascript:')) {

                        e.preventDefault();

                        // 显示加载条并开始进度动画
                        $loadingBar.addClass('active');

                        // 模拟进度更新
                        setTimeout(() => {
                            $loadingBar.addClass('progress-30');
                        }, 100);

                        setTimeout(() => {
                            $loadingBar.addClass('progress-60');
                        }, 200);

                        // 延迟跳转
                        setTimeout(() => {
                            window.location.href = href;
                        }, 300);
                    }
                } catch (error) {
                    // 如果URL解析失败，检查是否为相对路径
                    if (!href.includes('://') &&
                        !href.startsWith('mailto:') &&
                        !href.startsWith('tel:')) {

                        e.preventDefault();
                        $loadingBar.addClass('active');

                        setTimeout(() => {
                            $loadingBar.addClass('progress-30');
                        }, 100);

                        setTimeout(() => {
                            $loadingBar.addClass('progress-60');
                        }, 200);

                        setTimeout(() => {
                            window.location.href = href;
                        }, 300);
                    }
                }
            }
        });

        // 页面加载完成时完成加载条动画
        $(window).on('load', () => {
            // 检查加载条是否存在且可见
            if ($loadingBar.length > 0) {
                // 如果加载条有active类，说明是跳转过来的
                if ($loadingBar.hasClass('active')) {
                    $loadingBar.addClass('progress-100');

                    // 延迟隐藏加载条，添加淡出效果
                    setTimeout(() => {
                        $loadingBar.addClass('fade-out');

                        // 等待淡出动画完成后再移除所有类
                        setTimeout(() => {
                            $loadingBar.removeClass('active progress-30 progress-60 progress-80 progress-100 fade-out');
                        }, 300);
                    }, 500);
                } else {
                    // 如果是直接访问页面，确保加载条是隐藏的
                    $loadingBar.removeClass('active progress-30 progress-60 progress-80 progress-100 fade-out');
                }

                // 页面切换后重新应用保存的主题状态
                const savedTheme = localStorage.getItem('theme') || 'light';
                App.setTheme(savedTheme);

                // 重新初始化主题功能，确保按钮正常工作
                App.initTheme();
            }
        });

        // DOM 准备完成时的中间进度
        $(document).ready(() => {
            if ($loadingBar.hasClass('active')) {
                $loadingBar.addClass('progress-80');
            }
        });
    },

    updateActiveNav() {
        // 更新导航菜单的激活状态
        const currentPath = window.location.pathname;
        $('.nav-link').removeClass('active');

        $('.nav-link').each(function() {
            const linkPath = new URL($(this).attr('href'), window.location.origin).pathname;
            if (linkPath === currentPath) {
                $(this).addClass('active');
            }
        });
    },

    bindEvents() {
        // PJAX 已经处理文章卡片点击，移除重复的处理器

        // 为分页按钮绑定事件 - 移除阻止默认行为的代码
        // PJAX 会自动处理分页链接
    }
};

